mapper.select:
SELECT d.*, dt.id AS dt_id, dt.name AS dt_name
FROM psychology_base.dictionary d LEFT JOIN psychology_base.dictionary_type dt ON d.dictionary_type_id = dt.id



        d.name LIKE #{dictionaryName}


      AND d.dictionary_type_id = #{dictionary.dictionaryTypeId}


        AND d.status = #{dictionary.status}


ORDER BY update_time desc

mapper.insert:
INSERT INTO psychology_base.dictionary(id, dictionary_type_id, name, value, status, create_by, create_time,
                                       update_by, update_time, version)
VALUES ( #{dictionary.id}
       , #{dictionary.dictionaryTypeId}
       , #{dictionary.name}
       , #{dictionary.value}
       , #{dictionary.status}
       , #{dictionary.createBy}
       , CURRENT_TIMESTAMP()
       , #{dictionary.updateBy}
       , CURRENT_TIMESTAMP()
       , 1)

mapper.update:
UPDATE psychology_base.dictionary
SET id                 = #{dictionary.id}
  , dictionary_type_id = #{dictionary.dictionaryTypeId}
  , name               = #{dictionary.name}
  , value              = #{dictionary.value}
  , status             = #{dictionary.status}
  , update_by          = #{dictionary.updateBy}
  , update_time        = CURRENT_TIMESTAMP()
  , version            = version + 1
WHERE id = #{dictionary.id}
  AND version = #{dictionary.version}

mapper.delete:
DELETE
FROM psychology_base.dictionary
WHERE id = #{dictionary.id}
  AND version = #{dictionary.version}
