mapper.select:
SELECT * FROM psychology_base.dictionary_type



        name LIKE #{dictionaryTypeName}


ORDER BY update_time desc

mapper.insert:
INSERT INTO psychology_base.dictionary_type(id, name, remark, create_by, create_time, update_by, update_time, version)
VALUES ( #{dictionaryType.id}
       , #{dictionaryType.name}
       , #{dictionaryType.remark}
       , #{dictionaryType.createBy}
       , CURRENT_TIMESTAMP()
       , #{dictionaryType.updateBy}
       , CURRENT_TIMESTAMP()
       , 1)

mapper.update:
UPDATE psychology_base.dictionary_type
SET name        = #{dictionaryType.name}
  , remark      = #{dictionaryType.remark}
  , update_by   = #{dictionaryType.updateBy}
  , update_time = CURRENT_TIMESTAMP()
  , version     = version + 1
WHERE id = #{dictionaryType.id}
  AND version = #{dictionaryType.version}

mapper.delete:
DELETE
FROM psychology_base.dictionary_type
WHERE id = #{dictionaryType.id}
  AND version = #{dictionaryType.version}
