mapper.select:
SELECT s.*, m.id AS m_id, m.name AS m_name, sd.id AS sd_id, sd.name AS sd_name FROM psychology_user_centre.student s LEFT JOIN psychology_base.major m ON s.major_id = m.id LEFT JOIN psychology_base.school_department sd ON s.school_department_id = sd.id
s.name LIKE #{studentName}
ORDER BY update_time desc

mapper.insert:
INSERT INTO psychology_user_centre.student
(id, account, code, name, major_id, school_department_id, grade, identity, sex, birthday, phone, email, source,
 address, politic, create_by, create_time, update_by, update_time, version)
VALUES ( #{student.id}
       , #{student.account}
       , #{student.code}
       , #{student.name}
       , #{student.majorId}
       , #{student.schoolDepartmentId}
       , #{student.grade}
       , #{student.identity}
       , #{student.sex}
       , #{student.birthday}
       , #{student.phone}
       , #{student.email}
       , #{student.source}
       , #{student.address}
       , #{student.politic}
       , #{student.createBy}
       , CURRENT_TIMESTAMP()
       , #{student.updateBy}
       , CURRENT_TIMESTAMP()
       , 1)

mapper.update:
UPDATE psychology_user_centre.student
SET account              = #{student.account}
  , code                 = #{student.code}
  , name                 = #{student.name}
  , major_id             = #{student.majorId}
  , school_department_id = #{student.schoolDepartmentId}
  , grade                = #{student.grade}
  , identity             = #{student.identity}
  , sex                  = #{student.sex}
  , birthday             = #{student.birthday}
  , phone                = #{student.phone}
  , email                = #{student.email}
  , source               = #{student.source}
  , address              = #{student.address}
  , politic              = #{student.politic}
  , update_by            = #{student.updateBy}
  , update_time          = CURRENT_TIMESTAMP()
  , version              = version + 1
WHERE id = #{student.id}
  AND version = #{student.version}

mapper.delete:
DELETE
FROM psychology_user_centre.student
WHERE id = #{student.id}
  AND version = #{student.version}
