                                                  , account = #{teacher.account}
                                                  , code = #{teacher.code}
                                                  , name = #{teacher.name}
                                                  , sex = #{teacher.sex}
                                                  , birthday = #{teacher.birthday}
                                                  , department_id = #{teacher.departmentId}
                                                  , title_id = #{teacher.titleId}
                                                  , oversea = #{teacher.oversea}
