mapper.select:
SELECT t1.*, t2.id AS t_id, t2.NAME AS title_name , d.id AS d_id, d.name AS department_name FROM psychology_user_centre.teacher t1 LEFT JOIN psychology_base.title t2 ON t1.title_id = t2.id LEFT JOIN psychology_base.department d ON t1.department_id = d.id
t1.name LIKE #{teacherName}
ORDER BY update_time desc

mapper.insert:
INSERT INTO psychology_user_centre.teacher
(id, account, code, name, sex, birthday, department_id, title_id, oversea, relation, politic, type, education,
 degree, degree_unit, research_direction, job_type, position, create_by, create_time, update_by, update_time,
 version)
VALUES ( #{teacher.id}
       , #{teacher.account}
       , #{teacher.code}
       , #{teacher.name}
       , #{teacher.sex}
       , #{teacher.birthday}
       , #{teacher.departmentId}
       , #{teacher.titleId}
       , #{teacher.oversea}
       , #{teacher.relation}
       , #{teacher.politic}
       , #{teacher.type}
       , #{teacher.education}
       , #{teacher.degree}
       , #{teacher.degreeUnit}
       , #{teacher.researchDirection}
       , #{teacher.jobType}
       , #{teacher.position}
       , #{teacher.createBy}
       , CURRENT_TIMESTAMP()
       , #{teacher.updateBy}
       , CURRENT_TIMESTAMP()
       , 1)

mapper.update:
UPDATE psychology_user_centre.teacher
SET account            = #{teacher.account}
  , code               = #{teacher.code}
  , name               = #{teacher.name}
  , sex                = #{teacher.sex}
  , birthday           = #{teacher.birthday}
  , department_id      = #{teacher.departmentId}
  , title_id           = #{teacher.titleId}
  , oversea            = #{teacher.oversea}
  , relation           = #{teacher.relation}
  , politic            = #{teacher.politic}
  , type               = #{teacher.type}
  , education          = #{teacher.education}
  , degree             = #{teacher.degree}
  , degree_unit        = #{teacher.degreeUnit}
  , research_direction = #{teacher.researchDirection}
  , job_type           = #{teacher.jobType}
  , position           = #{teacher.position}
  , update_by          = #{teacher.updateBy}
  , update_time        = CURRENT_TIMESTAMP()
  , version            = version + 1
WHERE id = #{teacher.id}
  AND version = #{teacher.version}

mapper.delete:
DELETE
FROM psychology_user_centre.teacher
WHERE id = #{teacher.id}
  AND version = #{teacher.version}
