mapper.select:
SELECT p.*, t_fa.id AS t_fa_id, t_fa.name AS t_fa_name, t_ca.id AS t_ca_id, t_ca.name AS t_ca_name, pl.id AS pl_id, pl.name AS pl_name FROM psychology_manager.paper p LEFT JOIN psychology_base.teacher t_fa ON p.first_author_id = t_fa.id LEFT JOIN psychology_base.teacher t_ca ON p.corresponding_author_id = t_ca.id LEFT JOIN psychology_base.paper_level pl ON p.paper_level_id = pl.id
p.name LIKE #{paperName}
AND p.create_by = #{paper.createBy}
AND p.status > 0
ORDER BY update_time desc

mapper.insert:
INSERT INTO psychology_manager.paper
(id, name, first_author_id, corresponding_author_id, publication, publish_date, publication_version, paper_level_id,
 status, return_message, create_by, create_time, update_by, update_time, version)
VALUES ( #{paper.id}
       , #{paper.name}
       , #{paper.firstAuthorId}
       , #{paper.correspondingAuthorId}
       , #{paper.publication}
       , #{paper.publishDate}
       , #{paper.publicationVersion}
       , #{paper.paperLevelId}
       , #{paper.status}
       , #{paper.returnMessage}
       , #{paper.createBy}
       , CURRENT_TIMESTAMP()
       , #{paper.updateBy}
       , CURRENT_TIMESTAMP()
       , 1)

mapper.update:
UPDATE psychology_manager.paper
SET id                      = #{paper.id}
  , name                    = #{paper.name}
  , first_author_id         = #{paper.firstAuthorId}
  , corresponding_author_id = #{paper.correspondingAuthorId}
  , publication             = #{paper.publication}
  , publish_date            = #{paper.publishDate}
  , publication_version     = #{paper.publicationVersion}
  , paper_level_id          = #{paper.paperLevelId}
  , status                  = #{paper.status}
  , return_message          = #{paper.returnMessage}
  , update_by               = #{paper.updateBy}
  , update_time             = CURRENT_TIMESTAMP()
  , version                 = version + 1
WHERE id = #{paper.id}
  AND version = #{paper.version}

mapper.delete:
DELETE
FROM psychology_manager.paper
WHERE id = #{paper.id}
  AND version = #{paper.version}
