mapper.select:
SELECT p.*, ps.id AS ps_id, ps.name AS ps_name, st.id AS st_id, st.name AS st_name, t.id AS t_id, t.name AS t_name FROM psychology_manager.project p LEFT JOIN psychology_base.project_source ps ON p.project_source_id = ps.id LEFT JOIN psychology_base.subject_type st ON p.subject_type_id = st.id LEFT JOIN psychology_base.teacher t ON p.manager_id = t.id
p.name LIKE #{projectName}
AND p.create_by = #{project.createBy}
AND p.status > 0
ORDER BY update_time desc

mapper.insert:
INSERT INTO psychology_manager.project (
  id,
  NAME,
  CODE,
  school_code,
  project_source_id,
  subject_type_id,
  project_start_date,
  project_end_date,
  money,
  manager_id,
  project_is_end,
  STATUS,
  return_message,
  create_by,
  create_time,
  update_by,
  update_time,
  version
)
VALUES
(#{project.id}
,#{project.name}
,#{project.code}
,#{project.schoolCode}
,#{project.projectSourceId}
,#{project.subjectTypeId}
,#{project.projectStartDate}
,#{project.projectEndDate}
,#{project.money}
,#{project.managerId}
,#{project.projectIsEnd}
,#{project.status}
,#{project.returnMessage}
,#{project.createBy}
,CURRENT_TIMESTAMP()
,#{project.updateBy}
,CURRENT_TIMESTAMP()
,1

)

mapper.update:
UPDATE psychology_manager.project
SET id = #{project.id}
  ,
    NAME = #{project.name}
  ,
    CODE = #{project.code}
  ,
    school_code = #{project.schoolCode}
  ,
    project_source_id = #{project.projectSourceId}
  ,
    subject_type_id = #{project.subjectTypeId}
  ,
    project_start_date = #{project.projectStartDate}
  ,
    project_end_date = #{project.projectEndDate}
  ,
    money = #{project.money}
  ,
    manager_id = #{project.managerId}
  ,
    project_is_end = #{project.projectIsEnd}
  ,
    STATUS = #{project.status}
  ,
    return_message = #{project.returnMessage}
  ,
    create_by = #{project.createBy}
  ,
    create_time = #{project.createTime}
  ,
    update_by = #{project.updateBy}
  ,
    update_time = CURRENT_TIMESTAMP()
  ,
    version = version + 1

WHERE
  id = #{project.id}

  AND version = #{project.version}
