mapper.select:
SELECT * FROM psychology_manage.project



    name LIKE #{projectName}


ORDER BY update_time desc

mapper.insert:
INSERT INTO psychology_manage.project(
                            id
                                        , name
                                        , code
                                        , school_code
                                        , project_source_id
                                        , subject_type_id
                                        , project_start_date
                                        , project_end_date
                                        , money
                                        , manager_id
                                        , project_is_end
                                        , status
                                        , return_message
                                        , create_by
                                        , create_time
                                        , update_by
                                        , update_time
                                        , version
                    )
VALUES (

                    #{project.id}

                    , #{project.name}

                    , #{project.code}

                    , #{project.schoolCode}

                    , #{project.projectSourceId}

                    , #{project.subjectTypeId}

                    , #{project.projectStartDate}

                    , #{project.projectEndDate}

                    , #{project.money}

                    , #{project.managerId}

                    , #{project.projectIsEnd}

                    , #{project.status}

                    , #{project.returnMessage}

                    , #{project.createBy}

                    , #{project.createTime}

                    , #{project.updateBy}

                    , #{project.updateTime}

                    , #{project.version}
                            )

mapper.update:
UPDATE psychology_manage.project
    SET
                                    id = #{project.id}
                                                  , name = #{project.name}
                                                  , code = #{project.code}
                                                  , school_code = #{project.schoolCode}
                                                  , project_source_id = #{project.projectSourceId}
                                                  , subject_type_id = #{project.subjectTypeId}
                                                  , project_start_date = #{project.projectStartDate}
                                                  , project_end_date = #{project.projectEndDate}
                                                  , money = #{project.money}
                                                  , manager_id = #{project.managerId}
                                                  , project_is_end = #{project.projectIsEnd}
                                                  , status = #{project.status}
                                                  , return_message = #{project.returnMessage}
                                                  , create_by = #{project.createBy}
                                                  , create_time = #{project.createTime}
                                                  , update_by = #{project.updateBy}
                                                  , update_time = #{project.updateTime}
                                                  , version = #{project.version}
                        WHERE id = #{project.id}
  AND version = #{project.version}
